import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminProductCreate.css';

const AdminProductCreate = () => {
  const [formData, setFormData] = useState({
    category_id: '',
    brand_id: '',
    name: '',
    volume: '',
    rating: '',
    views: '',
    code: '',
    description: '',
    description_full: '',
    composition: '',
    usage: '',
    features: {
      brand: '',
      type: '',
      class: '',
      country: '',
      category: '',
      purpose: '',
      gender: '',
      active_ingredients: '',
    },
    store_prices: [{ store_id: '', price: '', link: '' }],
    images: [],
  });

  const [categories, setCategories] = useState([]);
  const [brands, setBrands] = useState([]);
  const [stores, setStores] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    // Fetch categories
    axios.get('https://your-api-url/categories')
      .then(response => setCategories(response.data))
      .catch(err => console.error('Помилка отримання категорій:', err));

    // Fetch brands
    axios.get('https://your-api-url/brands')
      .then(response => setBrands(response.data))
      .catch(err => console.error('Помилка отримання брендів:', err));

    // Fetch stores
    axios.get('https://your-api-url/stores')
      .then(response => setStores(response.data))
      .catch(err => console.error('Помилка отримання магазинів:', err));
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleFeaturesChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      features: {
        ...prev.features,
        [name]: value,
      },
    }));
  };

  const handleStorePriceChange = (index, e) => {
    const { name, value } = e.target;
    setFormData(prev => {
      const store_prices = [...prev.store_prices];
      store_prices[index] = { ...store_prices[index], [name]: value };
      return { ...prev, store_prices };
    });
  };

  const addStorePrice = () => {
    setFormData(prev => ({
      ...prev,
      store_prices: [...prev.store_prices, { store_id: '', price: '', link: '' }],
    }));
  };

  const removeStorePrice = (index) => {
    setFormData(prev => ({
      ...prev,
      store_prices: prev.store_prices.filter((_, i) => i !== index),
    }));
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setFormData(prev => ({
      ...prev,
      images: files,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    const formDataToSend = new FormData();

    // Basic fields
    formDataToSend.append('category_id', formData.category_id);
    formDataToSend.append('brand_id', formData.brand_id);
    formDataToSend.append('name', formData.name);
    formDataToSend.append('volume', formData.volume);
    formDataToSend.append('rating', formData.rating);
    formDataToSend.append('views', formData.views);
    formDataToSend.append('code', formData.code);
    formDataToSend.append('description', formData.description);
    formDataToSend.append('description_full', formData.description_full);
    formDataToSend.append('composition', formData.composition);
    formDataToSend.append('usage', formData.usage);
    formDataToSend.append('features', JSON.stringify(formData.features));

    // Store prices
    formData.store_prices.forEach((store, index) => {
      if (store.store_id && store.price) {
        formDataToSend.append(`store_prices[${index}][store_id]`, store.store_id);
        formDataToSend.append(`store_prices[${index}][price]`, store.price);
        if (store.link) {
          formDataToSend.append(`store_prices[${index}][link]`, store.link);
        }
      }
    });

    // Images
    formData.images.forEach((image) => {
      if (image) {
        formDataToSend.append('images', image);
      }
    });

    // Debug FormData
    const formDataEntries = {};
    for (const [key, value] of formDataToSend.entries()) {
      formDataEntries[key] = value;
    }
    console.log('FormData:', formDataEntries);

    try {
      const response = await axios.post('https://your-api-url/admin/product', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      console.log('Товар створено:', response.data);
      setSuccess('Товар успішно створено!');
      setFormData({
        category_id: '',
        brand_id: '',
        name: '',
        volume: '',
        rating: '',
        views: '',
        code: '',
        description: '',
        description_full: '',
        composition: '',
        usage: '',
        features: {
          brand: '',
          type: '',
          class: '',
          country: '',
          category: '',
          purpose: '',
          gender: '',
          active_ingredients: '',
        },
        store_prices: [{ store_id: '', price: '', link: '' }],
        images: [],
      });
    } catch (error) {
      console.error('Помилка створення товару:', error.response?.data || error.message);
      setError(error.response?.data?.error || 'Помилка створення товару');
    }
  };

  return (
    <div className="admin-product-create">
      <h2>Створити новий товар</h2>
      {error && <p className="error">{error}</p>}
      {success && <p className="success">{success}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Категорія:</label>
          <select name="category_id" value={formData.category_id} onChange={handleInputChange} required>
            <option value="">Виберіть категорію</option>
            {categories.map(cat => (
              <option key={cat.id} value={cat.id}>{cat.name_ua}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Бренд:</label>
          <select name="brand_id" value={formData.brand_id} onChange={handleInputChange} required>
            <option value="">Виберіть бренд</option>
            {brands.map(brand => (
              <option key={brand.id} value={brand.id}>{brand.name}</option>
            ))}
          </select>
        </div>
        <div>
          <label>Назва:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Об'єм:</label>
          <input
            type="text"
            name="volume"
            value={formData.volume}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Рейтинг:</label>
          <input
            type="number"
            step="0.1"
            name="rating"
            value={formData.rating}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Перегляди:</label>
          <input
            type="number"
            name="views"
            value={formData.views}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Код товару:</label>
          <input
            type="text"
            name="code"
            value={formData.code}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Короткий опис:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Повний опис:</label>
          <textarea
            name="description_full"
            value={formData.description_full}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Склад:</label>
          <textarea
            name="composition"
            value={formData.composition}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Використання:</label>
          <textarea
            name="usage"
            value={formData.usage}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <h3>Характеристики</h3>
          <div>
            <label>Бренд (характеристика):</label>
            <input
              type="text"
              name="brand"
              value={formData.features.brand}
              onChange={handleFeaturesChange}
            />
          </div>
          <div>
            <label>Тип:</label>
            <input
              type="text"
              name="type"
              value={formData.features.type}
              onChange={handleFeaturesChange}
            />
          </div>
          <div>
            <label>Клас:</label>
            <input
              type="text"
              name="class"
              value={formData.features.class}
              onChange={handleFeaturesChange}
            />
          </div>
          <div>
            <label>Країна:</label>
            <input
              type="text"
              name="country"
              value={formData.features.country}
              onChange={handleFeaturesChange}
            />
          </div>
          <div>
            <label>Категорія (характеристика):</label>
            <input
              type="text"
              name="category"
              value={formData.features.category}
              onChange={handleFeaturesChange}
            />
          </div>
          <div>
            <label>Призначення:</label>
            <input
              type="text"
              name="purpose"
              value={formData.features.purpose}
              onChange={handleFeaturesChange}
            />
          </div>
          <div>
            <label>Стать:</label>
            <input
              type="text"
              name="gender"
              value={formData.features.gender}
              onChange={handleFeaturesChange}
            />
          </div>
          <div>
            <label>Активні інгредієнти:</label>
            <input
              type="text"
              name="active_ingredients"
              value={formData.features.active_ingredients}
              onChange={handleFeaturesChange}
            />
          </div>
        </div>
        <div>
          <h3>Ціни в магазинах</h3>
          {formData.store_prices.map((store, index) => (
            <div key={index} className="store-price">
              <select
                name="store_id"
                value={store.store_id}
                onChange={(e) => handleStorePriceChange(index, e)}
                required
              >
                <option value="">Виберіть магазин</option>
                {stores.map(s => (
                  <option key={s.id} value={s.id}>{s.name}</option>
                ))}
              </select>
              <input
                type="number"
                name="price"
                value={store.price}
                onChange={(e) => handleStorePriceChange(index, e)}
                placeholder="Ціна"
                required
              />
              <input
                type="text"
                name="link"
                value={store.link}
                onChange={(e) => handleStorePriceChange(index, e)}
                placeholder="Посилання"
              />
              <button type="button" onClick={() => removeStorePrice(index)}>
                Видалити
              </button>
            </div>
          ))}
          <button type="button" onClick={addStorePrice}>
            Додати ціну
          </button>
        </div>
        <div>
          <label>Зображення:</label>
          <input
            type="file"
            multiple
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>
        <button type="submit">Створити товар</button>
      </form>
    </div>
  );
};

export default AdminProductCreate;
