import React, { useState, useEffect } from 'react';
import { useParams, useLocation, Link } from 'react-router-dom';
import products from '../data/products';
import './ProductList.css';

function ProductList({ searchTerm }) {
  const { categoryId } = useParams();
  const location = useLocation();
  
  // Отримуємо query-параметр type
  const queryParams = new URLSearchParams(location.search);
  const initialType = queryParams.get('type') || '';

  // Початковий стан фільтрів
  const [filters, setFilters] = useState({
    brands: [],
    priceRanges: [
      { label: '0-100', min: 0, max: 100 },
      { label: '100-200', min: 100, max: 200 },
      { label: '200-300', min: 200, max: 300 },
      { label: '300-400', min: 300, max: 400 },
      { label: '400-500', min: 400, max: 500 },
      { label: '500-600', min: 500, max: 600 },
      { label: '600-700', min: 600, max: 700 },
      { label: '700-800', min: 700, max: 800 },
      { label: '800-900', min: 800, max: 900 },
      { label: '900-1000', min: 900, max: 1000 },
      { label: '1000+', min: 1000, max: Infinity },
    ],
    volumes: [],
    types: [],
  });

  // Стан для відфільтрованих товарів
  const [filteredProducts, setFilteredProducts] = useState([]);

  // Стан для розгортання/згортання фільтрів
  const [showMore, setShowMore] = useState({
    brands: false,
    priceRanges: false,
    volumes: false,
    types: false,
  });

  // Отримуємо унікальні значення для фільтрів
  useEffect(() => {
    const categoryProducts = products.filter(product => product.category === categoryId);

    // Унікальні бренди
    const uniqueBrands = [...new Set(categoryProducts.map(product => product.specs.brand))];
    
    // Унікальні об’єми
    const uniqueVolumes = [...new Set(categoryProducts
      .filter(product => product.specs.volume)
      .map(product => product.specs.volume))];
    
    // Унікальні типи
    const uniqueTypes = [...new Set(categoryProducts
      .filter(product => product.specs.type)
      .map(product => product.specs.type))];

    // Оновлюємо фільтри
    setFilters(prevFilters => ({
      ...prevFilters,
      brands: uniqueBrands,
      volumes: uniqueVolumes,
      types: uniqueTypes,
    }));

    // Початкова фільтрація
    let initialFiltered = categoryProducts
      .filter(product => product.name.toLowerCase().includes(searchTerm.toLowerCase()));

    // Якщо є initialType, застосовуємо фільтр за типом
    if (initialType) {
      initialFiltered = initialFiltered.filter(product =>
        product.specs.type === initialType
      );
    }

    setFilteredProducts(initialFiltered);

    // Автоматично вибираємо чекбокс для типу, якщо він є в URL
    if (initialType) {
      setTimeout(() => {
        const typeCheckbox = document.querySelector(`input[name="type"][value="${initialType}"]`);
        if (typeCheckbox) {
          typeCheckbox.checked = true;
        }
      }, 0);
    }
  }, [categoryId, searchTerm, initialType]);

  // Обробка вибору фільтрів
  const handleFilterChange = (filterType, value) => {
    let updatedProducts = products.filter(product => product.category === categoryId);

    // Фільтрація за брендом
    const selectedBrands = document.querySelectorAll('input[name="brand"]:checked');
    const selectedBrandValues = Array.from(selectedBrands).map(input => input.value);
    if (selectedBrandValues.length > 0) {
      updatedProducts = updatedProducts.filter(product =>
        selectedBrandValues.includes(product.specs.brand)
      );
    }

    // Фільтрація за ціною
    const selectedPriceRanges = document.querySelectorAll('input[name="priceRange"]:checked');
    const selectedPriceRangeValues = Array.from(selectedPriceRanges).map(input => input.value);
    if (selectedPriceRangeValues.length > 0) {
      updatedProducts = updatedProducts.filter(product => {
        return selectedPriceRangeValues.some(rangeLabel => {
          const range = filters.priceRanges.find(r => r.label === rangeLabel);
          return product.price >= range.min && product.price < range.max;
        });
      });
    }

    // Фільтрація за об’ємом
    const selectedVolumes = document.querySelectorAll('input[name="volume"]:checked');
    const selectedVolumeValues = Array.from(selectedVolumes).map(input => input.value);
    if (selectedVolumeValues.length > 0) {
      updatedProducts = updatedProducts.filter(product =>
        product.specs.volume && selectedVolumeValues.includes(product.specs.volume)
      );
    }

    // Фільтрація за типом
    const selectedTypes = document.querySelectorAll('input[name="type"]:checked');
    const selectedTypeValues = Array.from(selectedTypes).map(input => input.value);
    if (selectedTypeValues.length > 0) {
      updatedProducts = updatedProducts.filter(product =>
        product.specs.type && selectedTypeValues.includes(product.specs.type)
      );
    }

    // Фільтрація за пошуковим терміном
    updatedProducts = updatedProducts.filter(product =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );

    setFilteredProducts(updatedProducts);
  };

  // Функція для розгортання/згортання
  const toggleShowMore = (filterType) => {
    setShowMore(prev => ({
      ...prev,
      [filterType]: !prev[filterType],
    }));
  };

  // Обмеження до 12 елементів
  const getVisibleItems = (items, filterType) => {
    if (showMore[filterType] || items.length <= 12) {
      return items;
    }
    return items.slice(0, 12);
  };

  // Функція для модифікації назви товару
  const getProductName = (product) => {
    return `${product.name} (${product.specs.volume})`;
  };

  return (
    <div className="product-list">
      <h2>Товари категорії</h2>
      <div className="product-list-container">
        {/* Колонка з фільтрами */}
        <div className="filters">
          <h3>Фільтри</h3>

          {/* Фільтр за брендом */}
          <div className="filter-section">
            <h4>Бренд</h4>
            <div className="filter-items">
              {getVisibleItems(filters.brands, 'brands').map((brand, index) => (
                <label
                  key={index}
                  className={brand.length > 16 ? 'span-two-columns' : ''}
                >
                  <input
                    type="checkbox"
                    name="brand"
                    value={brand}
                    onChange={() => handleFilterChange('brand', brand)}
                  />
                  {brand}
                </label>
              ))}
            </div>
            {filters.brands.length > 12 && (
              <button
                className="show-more-btn"
                onClick={() => toggleShowMore('brands')}
              >
                {showMore.brands ? 'Менше ↑' : 'Більше ↓'}
              </button>
            )}
          </div>

          {/* Фільтр за ціною */}
          <div className="filter-section">
            <h4>Ціна (грн)</h4>
            <div className="filter-items">
              {getVisibleItems(filters.priceRanges, 'priceRanges').map((range, index) => (
                <label key={index}>
                  <input
                    type="checkbox"
                    name="priceRange"
                    value={range.label}
                    onChange={() => handleFilterChange('priceRange', range.label)}
                  />
                  {range.label}
                </label>
              ))}
            </div>
            {filters.priceRanges.length > 12 && (
              <button
                className="show-more-btn"
                onClick={() => toggleShowMore('priceRanges')}
              >
                {showMore.priceRanges ? 'Менше ↑' : 'Більше ↓'}
              </button>
            )}
          </div>

          {/* Фільтр за об’ємом */}
          <div className="filter-section">
            <h4>Об’єм</h4>
            <div className="filter-items">
              {getVisibleItems(filters.volumes, 'volumes').map((volume, index) => (
                <label key={index}>
                  <input
                    type="checkbox"
                    name="volume"
                    value={volume}
                    onChange={() => handleFilterChange('volume', volume)}
                  />
                  {volume}
                </label>
              ))}
            </div>
            {filters.volumes.length > 12 && (
              <button
                className="show-more-btn"
                onClick={() => toggleShowMore('volumes')}
              >
                {showMore.volumes ? 'Менше ↑' : 'Більше ↓'}
              </button>
            )}
          </div>

          {/* Фільтр за типом */}
          <div className="filter-section">
            <h4>Тип</h4>
            <div className="filter-items filter-items-single-column">
              {getVisibleItems(filters.types, 'types').map((type, index) => (
                <label key={index}>
                  <input
                    type="checkbox"
                    name="type"
                    value={type}
                    onChange={() => handleFilterChange('type', type)}
                  />
                  {type}
                </label>
              ))}
            </div>
            {filters.types.length > 12 && (
              <button
                className="show-more-btn"
                onClick={() => toggleShowMore('types')}
              >
                {showMore.types ? 'Менше ↑' : 'Більше ↓'}
              </button>
            )}
          </div>
        </div>

        {/* Колонка з товарами */}
        <div className="products">
          {filteredProducts.length > 0 ? (
            filteredProducts.map((product) => (
              <Link to={`/product/${product.id}`} key={product.id} className="product-card">
                <h3>{getProductName(product)}</h3>
                <p className="price">{product.price} грн</p>
                <img
                  src={product.images && product.images.length > 0 ? product.images[0] : product.image}
                  alt={product.name}
                />
                <p>{product.description}</p>
              </Link>
            ))
          ) : (
            <p>Товари не знайдено</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default ProductList;
