import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useParams } from 'react-router-dom';
import products from '../data/products';
import './ProductDetail.css';

function ProductDetail() {
  const { productId } = useParams();
  const product = products.find((p) => p.id === parseInt(productId));

  // Стан для поточного зображення
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  // Стан для попереднього зображення (для анімації)
  const [prevImageIndex, setPrevImageIndex] = useState(null);
  // Стан для напрямку анімації (right/left)
  const [direction, setDirection] = useState(null);
  // Стан для модального вікна на весь екран
  const [isFullScreen, setIsFullScreen] = useState(false);

  // Рефи для скролу до секцій
  const featuresRef = useRef(null);
  const descriptionRef = useRef(null);

  // Масив зображень
  const images = product ? (product.images || [product.image]) : [];

  // Обробка стрілок з useCallback
  const handlePrevImage = useCallback(
    (e) => {
      e.stopPropagation();
      setDirection('left');
      setPrevImageIndex(currentImageIndex);
      setCurrentImageIndex((prev) => (prev === 0 ? images.length - 1 : prev - 1));
      setTimeout(() => {
        setPrevImageIndex(null);
        setDirection(null);
      }, 500);
    },
    [currentImageIndex, images.length]
  );

  const handleNextImage = useCallback(
    (e) => {
      e.stopPropagation();
      setDirection('right');
      setPrevImageIndex(currentImageIndex);
      setCurrentImageIndex((prev) => (prev === images.length - 1 ? 0 : prev + 1));
      setTimeout(() => {
        setPrevImageIndex(null);
        setDirection(null);
      }, 500);
    },
    [currentImageIndex, images.length]
  );

  // Підтримка клавіатури для повноекранного режиму
  useEffect(() => {
    if (!product) return;

    const handleKeyDown = (e) => {
      if (!isFullScreen) return;
      if (e.key === 'ArrowLeft') {
        handlePrevImage(e);
      } else if (e.key === 'ArrowRight') {
        handleNextImage(e);
      } else if (e.key === 'Escape') {
        setIsFullScreen(false);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => window.removeEventListener('keydown', handleKeyDown);
  }, [isFullScreen, product, handlePrevImage, handleNextImage]);

  if (!product) {
    return <div>Товар не знайдено</div>;
  }

  // Обробка перемикання зображень через іконки
  const handleImageClick = (index) => {
    if (index !== currentImageIndex) {
      setDirection(index > currentImageIndex ? 'right' : 'left');
      setPrevImageIndex(currentImageIndex);
      setCurrentImageIndex(index);
      setTimeout(() => {
        setPrevImageIndex(null);
        setDirection(null);
      }, 500);
    }
  };

  // Відкриття/закриття повноекранного режиму
  const toggleFullScreen = () => {
    setIsFullScreen(!isFullScreen);
    if (isFullScreen) {
      setPrevImageIndex(null);
      setDirection(null);
    }
  };

  // Генерація зірочок для оцінки
  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 >= 0.5;

    for (let i = 0; i < 5; i++) {
      if (i < fullStars) {
        stars.push(<span key={i} className="star full">★</span>);
      } else if (i === fullStars && hasHalfStar) {
        stars.push(<span key={i} className="star half">★</span>);
      } else {
        stars.push(<span key={i} className="star empty">☆</span>);
      }
    }
    return stars;
  };

  // Обробка кліку по вкладках із плавним скролом
  const scrollToSection = (ref) => {
    ref.current.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="product-detail">
      {/* Вкладки */}
      <div className="tabs">
        <button
          className="tab-button"
          onClick={() => scrollToSection(featuresRef)}
          aria-label="Перейти до характеристик"
        >
          Характеристики
        </button>
        <button
          className="tab-button"
          onClick={() => scrollToSection(descriptionRef)}
          aria-label="Перейти до опису товару"
        >
          Опис товару
        </button>
      </div>

      <div className="product-content">
        {/* Блок із зображеннями */}
        <div className="image-gallery">
          <div className="thumbnail-gallery">
            {images.slice(0, 4).map((img, index) => (
              <div
                key={index}
                className={`thumbnail ${index === currentImageIndex ? 'active' : ''}`}
                onClick={() => handleImageClick(index)}
              >
                <img src={img} alt={`${product.name} thumbnail ${index + 1}`} />
              </div>
            ))}
            {images.length > 4 && (
              <div className="thumbnail more-images" onClick={() => handleImageClick(4)}>
                <img src={images[4]} alt={`${product.name} more`} />
                <span className="more-count">+{images.length - 4}</span>
              </div>
            )}
          </div>
          <div className="main-image-wrapper" onClick={toggleFullScreen}>
            <div className="image-container">
              {prevImageIndex !== null && (
                <img
                  src={images[prevImageIndex]}
                  alt={`${product.name} ${prevImageIndex + 1}`}
                  className={`main-image main-image-prev ${
                    direction === 'right' ? 'slide-out-left' : 'slide-out-right'
                  }`}
                />
              )}
              <img
                src={images[currentImageIndex]}
                alt={`${product.name} ${currentImageIndex + 1}`}
                className={`main-image ${
                  direction === 'right' ? 'slide-in-right' : direction === 'left' ? 'slide-in-left' : ''
                }`}
              />
            </div>
            {images.length > 1 && (
              <>
                <div className="arrow prev-arrow" onClick={handlePrevImage}>
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="white">
                    <path d="M15.41 16.59L10.83 12l4.58-4.59L14 6l-6 6 6 6 1.41-1.41z"/>
                  </svg>
                </div>
                <div className="arrow next-arrow" onClick={handleNextImage}>
                  <svg width="24" height="24" viewBox="0 0 24 24" fill="white">
                    <path d="M8.59 16.59L13.17 12 8.59 7.41 10 6l6 6-6 6-1.41-1.41z"/>
                  </svg>
                </div>
              </>
            )}
          </div>
        </div>

        {/* Інформація про продукт */}
        <div className="product-info">
          <h2>{product.name}</h2>
          <div className="product-meta">
            <div className="rating">{renderStars(product.rating || 0)}</div>
            <div className="views">{product.views || 0} переглядів</div>
            <div className="product-code">Код: {product.code || 'Н/Д'}</div>
          </div>
          <div className="price-container">
            <p className="price">{product.price} грн</p>
            <button className="price-view-button">Перегляд цін</button>
          </div>
          <p className="volume">Об'єм: {product.specs.volume || 'Н/Д'}</p>
        </div>
      </div>

      {/* Характеристики */}
      <div className="product-features" ref={featuresRef}>
        <h3>Характеристики</h3>
        <ul>
          <li>
            <span className="feature-key">Бренд</span>
            <span className="feature-value">{product.features?.brand || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Країна виробництва</span>
            <span className="feature-value">{product.features?.country || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Вид</span>
            <span className="feature-value">{product.features?.type || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Клас</span>
            <span className="feature-value">{product.features?.class || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Тип волосся / проблема</span>
            <span className="feature-value">{product.features?.hairType || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Особливості</span>
            <span className="feature-value">{product.features?.features || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Група товару</span>
            <span className="feature-value">{product.features?.category || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Призначення</span>
            <span className="feature-value">{product.features?.purpose || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Стать</span>
            <span className="feature-value">{product.features?.gender || 'Н/Д'}</span>
          </li>
          <li>
            <span className="feature-key">Активний компонент</span>
            <span className="feature-value">{product.features?.activeIngredients || 'Н/Д'}</span>
          </li>
        </ul>
      </div>

      {/* Опис товару */}
      <div className="product-description" ref={descriptionRef}>
        <h3>Опис товару</h3>
        <div className="description-section">
          <h4>Опис</h4>
          <p>{product.features.description || 'Н/Д'}</p>
        </div>
        <div className="description-section">
          <h4>Склад</h4>
          <p>{product.composition || 'Н/Д'}</p>
        </div>
        <div className="description-section">
          <h4>Використання</h4>
          <p>{product.usage || 'Н/Д'}</p>
        </div>
      </div>

      {/* Ціни в магазинах */}
      <div className="store-prices">
        <h3>Ціни в різних магазинах</h3>
        <div className="store-list">
          {product.storePrices?.map((store, index) => (
            <div key={index} className="store-item">
              <img
                src={store.logo}
                alt={`${store.name} logo`}
                className="store-logo"
                onError={(e) => (e.target.src = 'https://via.placeholder.com/50')}
              />
              <div className="store-details">
                <p className="store-name">{store.name}</p>
                <p className="store-price">{store.price} грн</p>
                {store.yearsWithUs >= 10 && (
                  <p className="store-years">З нами більше 10-ти років</p>
                )}
                <p className="store-delivery">Доставка: {store.delivery}</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Модальне вікно для повноекранного перегляду */}
      {isFullScreen && (
        <div className="fullscreen-modal" onClick={toggleFullScreen}>
          <div className="fullscreen-content">
            <div className="image-container">
              {prevImageIndex !== null && (
                <img
                  src={images[prevImageIndex]}
                  alt={`${product.name} fullscreen ${prevImageIndex + 1}`}
                  className={`fullscreen-image fullscreen-image-prev ${
                    direction === 'right' ? 'slide-out-left' : 'slide-out-right'
                  }`}
                />
              )}
              <img
                src={images[currentImageIndex]}
                alt={`${product.name} fullscreen ${currentImageIndex + 1}`}
                className={`fullscreen-image ${
                  direction === 'right' ? 'slide-in-right' : direction === 'left' ? 'slide-in-left' : ''
                }`}
              />
            </div>
            {images.length > 1 && (
              <>
                <div
                  className="fullscreen-arrow fullscreen-prev"
                  onClick={(e) => {
                    e.stopPropagation();
                    handlePrevImage(e);
                  }}
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" fill="white">
                    <path d="M15.41 16.59L10.83 12l4.58-4.59L14 6l-6 6 6 6 1.41-1.41z"/>
                  </svg>
                </div>
                <div
                  className="fullscreen-arrow fullscreen-next"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleNextImage(e);
                  }}
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" fill="white">
                    <path d="M8.59 16.59L13.17 12 8.59 7.41 10 6l6 6-6 6-1.41-1.41z"/>
                  </svg>
                </div>
              </>
            )}
            <button className="close-fullscreen" onClick={toggleFullScreen}>
              ✕
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default ProductDetail;
